import React from 'react';
import ChatBot from 'react-simple-chatbot';
const config ={
    width: "400px",
    height: "500px",
    floating: true,
  };
const Chatbot = () => {
    return (
        <div>
           <ChatBot
           steps={[
               {
                   id:'intro',
                   message:'Hello Recruiter. Thank You for Visitng my portfolio.',
                   trigger:'intro1'
               },
               {
                id:'intro1', 
                message:'Do you agree to the Terms and Conditions?', 
                trigger:'intro-user',
               },
               {
                id:'intro-user', 
                options:[
                  {value:'y', label:'Yes', trigger:'yes-response'},
                  {value:'n', label:'No', trigger:'no-response'},
                ] 
               },
               {
                id:'yes-response', 
                message:'Great!', 
                end:true,
               },
               {
                id:'no-response', 
                message:'Sorry to hear that.', 
                end:true,
               },
           ]}
           {...config}
           /> 
        </div>
    )
}
export default Chatbot
