import React from 'react';
import ChatBot from 'react-simple-chatbot';
// import { ThemeProvider } from 'styled-components';



const config ={
    width: "500px",
    height: "500px",
    floating: true,
  };
const Chatbot = () => {
    return (
        <div>
           <ChatBot headerTitle="Bot" enableSmoothScroll="true"

           steps={[
               {
                   id:'intro',
                   message:'Hello Recruiter. Thank You for Visitng my portfolio.',
                   trigger:'intro1',
                   user:true
               },
               {
                id:'intro1', 
                message:'What you want to know about', 
                trigger:'intro-user',
                user:true,
               },
               {
                id:'intro-user', 
                options:[
                  {value:'y', label:'Current Organization', trigger:'current-organization'},
                  {value:'n', label:'Notice Period', trigger:'Notice-period'},
                ] 
               },
               {
                id:'current-organization', 
                message:'I am currently working in TCS', 
                end:true,
               },
               {
                id:'Notice-period', 
                message:'I have to serve 3 months Notice Period', 
                trigger:'2',
               },
                {
                id: '2',
                component: (
                  <div> This is an example component<button value="yes" >Javascript</button> </div>

                ),
                end: true,
              }
           ]}
           {...config}
           />
        </div>
    )
}
export default Chatbot
