import React from 'react';
import ChatBot from 'react-simple-chatbot';
// import { ThemeProvider } from 'styled-components';
import resume from './ShivamGuptaResume.pdf'


const config ={
    width: "500px",
    height: "500px",
    floating: true,
  };
const Chatbot = () => {
    var trig=''
    return (
        <div>
           <ChatBot headerTitle="Bot" enableSmoothScroll="true"

           steps={[
               {
                   id:'intro',
                   message:'Hello Recruiter. Thank You for Visitng my portfolio.',
                   trigger:'age',
               },
               {
                id: 'age',
                user: true,
                trigger: '7',
                validator: (value) => {
                  if (isNaN(value)) {
                    return 'value must be a number';
                  } else if (value < 0) {
                    return 'value must be positive';
                  } else if (value > 120) {
                    return `${value}? Come on!`;
                  }

                  return true;
                },
              },
              {
                id: '7',
                message: 'Great! Check out your summary',
                end:true,
              },
               {
                id: 'user-input',
                user: true,

                validator:(value)=>{
                        if(value.includes("Resume")){
                        trig='send-resume'
                    }
                },
                trigger:trig,
            },
               {
                id:'intro1', 
                message:'What you want to know about', 
                trigger:'intro-user',
                user:true,
               },
               {
                id:'intro-user', 
                options:[
                  {value:'y', label:'Current Organization', trigger:'current-organization'},
                  {value:'n', label:'Notice Period', trigger:'Notice-period'},
                ] 
               },
               {
                id:'current-organization', 
                message:'I am currently working in TCS', 
                end:true,
               },
               {
                id:'Notice-period', 
                message:'I have to serve 3 months Notice Period', 
                end:true,
               },
                {
                id: 'send-resume',
                component: (
                  <div> <a href={resume} className="nav__link pulsingButton" download><i class="fa fa-download"></i>Resume</a> </div>

                ),
                end: true,
              }
           ]}
           {...config}
           />
        </div>
    )
}
export default Chatbot
