import React,{useState,useEffect} from 'react'
const Showthequery = () => {
    const [items, setitems] = useState({title:String,Content:String,_id:String})
useEffect(() => {
    fetch('/items').then((res)=>{
        if(res.ok){
            return res.json();
        }
    })
    .then((jsonRes)=>{
        console.log(jsonRes);
        setitems(jsonRes)})
    .catch((err)=>{console.log(err)});
}, [items]);
    return (
        <div>
            {items.map((item)=>{
                return(
                    <div key={item._id}>
                    <h1>{item.title}</h1>
                    <h1>{item.content}</h1>
                    </div>
                )
            })}
        </div>
    )
}


export default Showthequery;
