import React from 'react';
import ChatBot from 'react-simple-chatbot';
import { ThemeProvider } from 'styled-components';

const theme = {
    background: '#f5f8fb',
    fontFamily: 'Helvetica Neue',
    headerBgColor: '#EF6C00',
    headerFontColor: '#fff',
    headerFontSize: '15px',
    botBubbleColor: '#EF6C00',
    botFontColor: '#fff',
    userBubbleColor: '#fff',
    userFontColor: '#4a4a4a',
  };

const config ={
    width: "500px",
    height: "500px",
    floating: true,
  };
const Chatbot = () => {
    return (
        <div>
           <ChatBot headerTitle="Shivam"

           steps={[
               {
                   id:'intro',
                   message:'Hello Recruiter. Thank You for Visitng my portfolio.',
                   trigger:'intro1'
               },
               {
                id:'intro1', 
                message:'What you want to know about', 
                trigger:'intro-user',
               },
               {
                id:'intro-user', 
                options:[
                  {value:'y', label:'My Current Organization', trigger:'current-organization'},
                  {value:'n', label:'My Notice Period', trigger:'Notice-period'},
                ] 
               },
               {
                id:'current-organization', 
                message:'I am currently working in TCS', 
                end:true,
               },
               {
                id:'Notice-period', 
                message:'I have to serve 3 months Notice Period', 
                end:true,
               },
           ]}
           {...config}
           /> 
        </div>
    )
}
export default Chatbot
